import {useQuery} from "@apollo/react-hooks";
import gql from "graphql-tag";
import styles from "../styles/main.module.css";

export const GET_VSCO = gql`
	query {
		vsco {
			id
			imageURL
			linkURL
		}
	}
`;

function VscoLoader() {
	const {loading, error, data} = useQuery(GET_VSCO);

	if (loading) return "";
	if (error) return `Error! ${error.message}`;

	return (
		<>
			{data.vsco.map(vsco => (
				<div style={{height: "29vh", width: "29vw"}}>
					<div key={vsco.id}>
						<a href={vsco.linkURL}>
							<img
								className={styles.vscoGridImage}
								src={vsco.imageURL}
								alt=""
							/>
						</a>
					</div>
					<div key={vsco.id} style={{height: "29vh", width: "29vw"}}>
						<a href={vsco.linkURL}>
							<img
								className={styles.vscoGridImage}
								src={vsco.imageURL}
								alt=""
							/>
						</a>
					</div>
					<div key={vsco.id} style={{height: "29vh", width: "29vw"}}>
						<img className={styles.vscoGridImage} src={vsco.imageURL} alt="" />
					</div>
				</div>
			))}
		</>
	);
}

export default VscoLoader;
